import React, { useMemo, useState } from "react";
import {
  Text,
  View,
  StyleSheet,
  SafeAreaView,
  Alert,
  ScrollView,
  Pressable,
  Dimensions,
} from "react-native";
import { AssetsSelector } from "expo-images-picker";
import { MediaType } from "expo-media-library";
import { useNavigation } from "@react-navigation/native";
import { Entypo, Ionicons, MaterialIcons, AntDesign } from "@expo/vector-icons";
import { colors } from "../../modal/color";
const MenuDetailsForDesktop = () => {
  const navigation = useNavigation();
  const windowWidth = Number(Dimensions.get("window").width);
  return (
    <View style={{ position: "absolute", top: -20, right: "9.2%" }}>
      <View
        style={{
          display: windowWidth > 800 ? "flex" : "none",
          width: 200,
          height: 500,
          backgroundColor: colors.secondary,
          borderTopLeftRadius: 5,
          borderBottomLeftRadius: 5,
          borderBottomRightRadius: 5,
          padding: 10,
          shadowColor: colors.black,
          shadowOffset: { width: 0, height: 1 },
          shadowOpacity: 0.9,
          shadowRadius: 5,
          elevation: 5,
        }}>
        <View>
          <Pressable
            style={{
              flexDirection: "row",
              justifyContent: "flex-start",
              alignItems: "center",
            }}>
            <Entypo name="chat" size={24} color={colors.white} />
            <Text style={{ color: colors.white }}>Chat</Text>
          </Pressable>
        </View>
      </View>
    </View>
  );
};

export default MenuDetailsForDesktop;
